import React, { useState } from "react";

import Carousel, { arrowsPlugin } from "@brainhubeu/react-carousel";
import "@brainhubeu/react-carousel/lib/style.css";
import TestimonialItem from "./TestimonialItem";
import { BackArrowIcon, NextArrowIcon } from "../Icons/ArrowIcons";

function TestimonialCarousel() {
  const [slides] = useState([
    {
      from: "Zemichael Tesfaye",
      text: `There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don't look even slightly believable. If you are going to use a passage of Lorem Ipsum, you need to be sure there isn't anything embarrassing.`,
    },
    {
      from: "Daniel Mulugeta",
      text: `The majority have suffered alteration in some form, by injected humour, or randomised words which don't look even slightly believable. If you are going to use a passage of Lorem Ipsum, you need to be sure there isn't anything embarrassing.`,
    },
  ]);

  return (
    <>
      <Carousel
        slides={slides.map((value, id) => (
          <TestimonialItem key={id} {...value} />
        ))}
        plugins={[
          {
            resolve: arrowsPlugin,
            options: {
              arrowLeft: <BackArrowIcon />,
              arrowRight: <NextArrowIcon />,
              addArrowClickHandler: true,
            },
          },
        ]}
      />
    </>
  );
}

export default TestimonialCarousel;
