import React from "react";
import styled,{useTheme} from "styled-components";
import Typography from "../Typography";

function Hero({ heading, page, image }) {
    const theme = useTheme();
    return (
        <MainWrapper bgImage={image}>
            <ContentWrapper>
                <Typography allCaps textAlign="center" as="h1">
                    {heading}
                </Typography>
                <Typography textAlign="center">
                    Home / <Typography color={theme.colors.primary} textAlign="center">{page}</Typography>
                </Typography>

            </ContentWrapper>
        </MainWrapper>
    );
}

const MainWrapper = styled.div`
  background: radial-gradient(rgba(07, 12, 18, 0.4), rgba(07, 12, 18, 1))
    ${(props) => `, url(${props.bgImage})`};
  background-size: cover;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 60vh;
`;

const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export default Hero;
